  let [formData, setFormData] = useState(
    {
      firstName: '',
      lastName: '',
      phone: '',
      email: '',
      password: '',
      confirmPassword: '',
    }
  );
  let [passwordForm, setPasswordForm] = useState(
    {
      password: '',
      confirmPassword: '',
    }
  );
  //console.log(passwordForm);
  function sendLoginData(event) {
    setPasswordForm(prev => {
      return {
        ...prev,
        [event.target.name] : event.target.value
      }
    }
