import React, { useState } from 'react'
import '../styles/Loginform.scss';

const FormHomeWork = (props) => {
  
 
  let [formData, setFormData] = useState(
    {
      firstName: '',
      lastName: '',
      phone: '',
      email: '',
    }
  );
  //console.log(formData);
  
  function getLoginData(event) {
    setFormData(prev => {
      return {
        ...prev,
        [event.target.name] : event.target.value
      }
    }
  )} 

  return (
    <div className='container'>
      <div className='wrapper'>
        <div className='title'>
           <h1>Login Form</h1>
        </div>
        <div className='form-container'>
          <input type='text' 
          className='login-input'
          placeholder='First Name' 
          name='firstName' 
          value={formData.firstName} 
          onChange={getLoginData} />

          <input type='text' 
          className='login-input'
          placeholder='Last Name' 
          name='lastName' 
          value={formData.lastName} 
          onChange={getLoginData} />

          <input type='text' 
          className='login-input'
          placeholder='Phone' 
          name='phone' 
          value={formData.phone} 
          onChange={getLoginData} />

          <input type='text' 
          className='login-input'
          placeholder='Email' 
          name='email' 
          value={formData.email}
          onChange={getLoginData}
          />

          <input type='password' 
          className='login-input'
          placeholder='Password' 
          name={props.passwordForm.password}
          value={props.passwordForm.password}
          onChange={getLoginData}
           />

          <input type='password' 
          className='login-input'
          placeholder='Confirm Password' 
          name={props.passwordForm.confirmPassword} 
          value={props.passwordForm.confirmPassword}
          onChange={getLoginData}
           />
        </div>
        <div className='submit'>
          <button className= 'submit-btn' onClick={props.handleClick}>Submit</button>
        </div>
      </div>
    </div>
  )
}

export default FormHomeWork
