import React, { useState} from 'react';
import Forma from './components/Forma';
import FormHomeWork from './components/FormHomeWork';

function App() {
  let [passwordForm, setPasswordForm] = useState(
    {
      password: '',
      confirmPassword: '',
    }
  );
  //console.log(passwordForm);
  function sendLoginData(event) {
    setPasswordForm(prev => {
      return {
        ...prev,
        [event.target.name] : event.target.value
      }
    }
    )
    if (passwordForm.password !== passwordForm.confirmPassword) {
      console.log('Password and Confirm Password are not the same');
    }
    event.preventDefault();
  }
      

  return (
    <div className="App">
      {/*<Forma />*/}
      <FormHomeWork handleClick={sendLoginData}/>
      
    </div>
  );
}

export default App;
